(1, 'PreviousPage', '$info = show_menu2(0, SM2_CURR, SM2_START, SM2_ALL|SM2_BUFFER, ''[if(class==menu-current){[level] [sib] [sibCount] [parent]}]'', '''', '''', '''');\rlist($nLevel, $nSib, $nSibCount, $nParent) = explode('' '', $info);\r\r// show previous\r$prv = $nSib > 1 ? $nSib - 1 : 0;\rif ($prv > 0) { \rreturn show_menu2(0, SM2_CURR, SM2_START, SM2_ALL|SM2_BUFFER, "[if(sib==$prv){[a][menu_title]</a> &lt;&lt;}]", '''', '''', '''');\r}\relse \rreturn ''(no previous)'';\r\r', 'Create a previous link to your page', 1366277972, 1, 1, 0, 0, 0, 'Display a link to the previous page on the same menu level'),
(2, 'NextPage', '$info = show_menu2(0, SM2_CURR, SM2_START, SM2_ALL|SM2_BUFFER, ''[if(class==menu-current){[level] [sib] [sibCount] [parent]}]'', '''', '''', '''');\rlist($nLevel, $nSib, $nSibCount, $nParent) = explode('' '', $info);\r\r// show next\r$nxt = $nSib < $nSibCount ? $nSib + 1 : 0;\rif ($nxt > 0) {\rreturn show_menu2(0, SM2_CURR, SM2_START, SM2_ALL|SM2_BUFFER,	"[if(sib==$nxt){&gt;&gt; [a][menu_title]</a>}]", '''', '''', '''');\r}\relse return ''(no next)'';\r', 'Create a next link to your page', 1366277972, 1, 1, 0, 0, 0, 'Display a link to the next page on the same menu level'),
(3, 'ParentPage', '$info = show_menu2(0, SM2_CURR, SM2_START, SM2_ALL|SM2_BUFFER, ''[if(class==menu-current){[level] [sib] [sibCount] [parent]}]'', '''', '''', '''');\rlist($nLevel, $nSib, $nSibCount, $nParent) = explode('' '', $info);\r\r// show up level\rif ($nLevel > 0) {\r$lev = $nLevel - 1;\rreturn show_menu2(0, SM2_ROOT, SM2_CURR, SM2_CRUMB|SM2_BUFFER, "[if(level==$lev){[a][menu_title]</a>}]", '''', '''', '''');\r}\relse \rreturn ''(no parent)'';\r\r', 'Create a parent link to your page', 1366277972, 1, 1, 0, 0, 0, 'Display a link to the parent page of the current page'),
