(5, 'Text2Image', '//clean up old files..\r\n$dir = WB_PATH.''/temp/'';\r\n$dp = opendir($dir) or die (''Could not open ''.$dir);\r\nwhile ($file = readdir($dp)) {\r\n	if ((preg_match(''/img_/'',$file)) && (filemtime($dir.$file)) <  (strtotime(''-10 minutes''))) {\r\n		unlink($dir.$file);\r\n	}\r\n}\r\nclosedir($dp);\r\n\r\n$imgfilename = ''img_''.rand().''_''.time().''.jpg'';\r\n//create image\r\n$padding = 0;\r\n$font = 3;  	\r\n\r\n$height = imagefontheight($font) + ($padding * 2);\r\n$width = imagefontwidth($font) * strlen($text) + ($padding * 2);\r\n$image_handle = imagecreatetruecolor($width, $height);\r\n$text_color = imagecolorallocate($image_handle, 0, 0, 0);\r\n$background_color = imagecolorallocate($image_handle, 255, 255, 255);\r\n$bg_height = imagesy($image_handle);\r\n$bg_width = imagesx($image_handle);\r\nimagefilledrectangle($image_handle, 0, 0, $bg_width, $bg_height, $background_color);\r\nimagestring($image_handle, $font, $padding, $padding, $text, $text_color);\r\nimagejpeg($image_handle,WB_PATH.''/temp/''.$imgfilename,100);\r\nimagedestroy($image_handle);\r\n\r\nreturn ''<img src="''.WB_URL.''/temp/''.$imgfilename.''" style="border:0px;margin:0px;padding:0px;vertical-align:middle;" />'';', 'Create an image from the textparameter', 1366277972, 1, 1, 0, 0, 0, 'Use [[text2image?text=The text to create]]'),
(6, 'EmailFilter', 'return '''';', 'Emailfiltering on your output - dummy Droplet', 1366277972, 1, 1, 0, 0, 0, 'usage:  [[EmailFilter]]'),
(7, 'RandomImage', '$folder=opendir(WB_PATH.MEDIA_DIRECTORY.''/''.$dir.''/.''); \r$names = array();\rwhile ($file = readdir($folder))  {\r	$ext=strtolower(substr($file,-4));\r	if ($ext==".jpg"||$ext==".gif"||$ext==".png"){\r		$names[count($names)] = $file; \r	}\r}\rclosedir($folder);\rshuffle($names);\r$image=$names[0]; \r$name=substr($image,0,-4);\rreturn ''<img src="''.WB_URL.MEDIA_DIRECTORY.''/''.$dir.''/''.$image.''" alt="''.$name.''" width="''.$width.''" height="''.$height.''"/>'';\r', 'Get a random image from a folder in the MEDIA folder.', 1366277972, 1, 1, 0, 0, 0, 'Commandline to use: [[RandomImage?dir=subfolder_in_mediafolder]]'),
(8, 'ShowWysiwyg', 'global $database;\r\n	$content = '''';\r\n	$section = isset($section) ? intval($section) : 0;\r\n	if ($section) {\r\n		if (is_readable(WB_PATH.''/modules/wysiwyg/view.php'')) {\r\n		// if valid section is given and module wysiwyg is installed\r\n			$iOldSectionId = intval($section_id); // save old SectionID\r\n			$section_id = $section;\r\n			ob_start(); // generate output by regulary wysiwyg module\r\n			require(WB_PATH.''/modules/wysiwyg/view.php'');\r\n			$content = ob_get_clean();\r\n			$section_id = $ioldSectionId; // restore old SectionId\r\n		}\r\n	}\r\nreturn $content;', 'Display one defined WYSIWYG section', 1366277972, 1, 1, 0, 0, 0, 'Use [[ShowSection?section=10]]'),
(9, 'Oneliner', '$line = file (dirname(__FILE__)."/example/oneliners.txt");\rshuffle($line);\rreturn $line[0]; \r', 'Create a random oneliner on your page', 1366277972, 1, 1, 0, 0, 0, 'Use: [[OneLiner]]. The file with the oneliner data is located in /modules/droplets/example/oneliners.txt'),
(10, 'ShowRandomWysiwyg', 'global $database;\r\n	$content = '''';\r\n	if (isset($section)) {\r\n		if( preg_match(''/^[0-9]+(?:\\s*[\\,\\|\\-\\;\\:\\+\\#\\/]\\s*[0-9]+\\s*)*$/'', $section)) {\r\n			if (is_readable(WB_PATH.''/modules/wysiwyg/view.php'')) {\r\n			// if valid arguments given and module wysiwyg is installed\r\n				// split and sanitize arguments\r\n				$aSections = preg_split(''/[\\s\\,\\|\\-\\;\\:\\+\\#\\/]+/'', $section);\r\n				$section_id = $aSections[array_rand($aSections)]; // get random element\r\n				ob_start(); // generate output by wysiwyg module\r\n				require(WB_PATH.''/modules/wysiwyg/view.php'');\r\n				$content = ob_get_clean();\r\n			}\r\n		}\r\n	}\r\nreturn $content;', 'Randomly display one WYSIWYG section from a given list', 1366277972, 1, 1, 0, 0, 0, 'Use [[ShowRandomWysiwyg?section=10,12,15,20]] possible Delimiters: [ ,;:|-+#/ ]'),
