(15, 'template', '', ''),
(16, 'module', 'form', 'a:6:{s:7:"page_id";s:7:"page_id";s:5:"title";s:10:"page_title";s:4:"link";s:4:"link";s:11:"description";s:11:"description";s:13:"modified_when";s:13:"modified_when";s:11:"modified_by";s:11:"modified_by";}'),
(17, 'query_start', 'SELECT [TP]pages.page_id, [TP]pages.page_title,	[TP]pages.link, [TP]pages.description, [TP]pages.modified_when, [TP]pages.modified_by	FROM [TP]mod_form_fields, [TP]mod_form_settings, [TP]pages WHERE ', 'form'),
(18, 'query_body', ' [TP]pages.page_id = [TP]mod_form_settings.page_id AND [TP]mod_form_settings.header LIKE ''%[STRING]%''\n    	OR [TP]pages.page_id = [TP]mod_form_settings.page_id AND [TP]mod_form_settings.footer LIKE ''%[STRING]%''\n    	OR [TP]pages.page_id = [TP]mod_form_fields.page_id AND [TP]mod_form_fields.title LIKE ''%[STRING]%'' ', 'form'),
(19, 'query_end', '', 'form'),
(20, 'module', 'code', 'a:6:{s:7:"page_id";s:7:"page_id";s:5:"title";s:10:"page_title";s:4:"link";s:4:"link";s:11:"description";s:11:"description";s:13:"modified_when";s:13:"modified_when";s:11:"modified_by";s:11:"modified_by";}'),
(21, 'query_start', 'SELECT [TP]pages.page_id, [TP]pages.page_title,	[TP]pages.link, [TP]pages.description, [TP]pages.modified_when, [TP]pages.modified_by	FROM [TP]mod_code, [TP]pages WHERE ', 'code'),
(22, 'query_body', ' [TP]pages.page_id = [TP]mod_code.page_id AND [TP]mod_code.content [O] ''[W][STRING][W]'' AND [TP]pages.searching = ''1''', 'code'),
(23, 'query_end', '', 'code'),
